package TERV;import java.util.Scanner;public class AreaOfIntrest {    public static void main(String[] args){        Scanner s=new Scanner(System.in);        double amount=s.nextDouble();        double intrest=s.nextDouble();        double year=s.nextDouble();        double totalIntrest=(amount*year*intrest)/100;        double discount=(2*totalIntrest)/100;        double total=totalIntrest+amount;        double profit=total-discount;        System.out.printf("%.2f\n",totalIntrest);        System.out.printf("%.2f\n",total);        System.out.printf("%.2f\n",discount);        System.out.printf("%.2f\n",profit);    }}